function closeMobMenu(){let e=$("body");$(document).on("click",".js-close-menu",function(o){o.preventDefault(),e.removeClass("opened-menu")})}function openMobMenu(){let e=$("body");$(document).on("click",".js-open-menu",function(o){o.preventDefault(),e.addClass("opened-menu")})}function closeTopPanel(){let e=$(".top-panel");$(document).on("click",".js-top-panel",function(o){o.preventDefault(),e.removeClass("is-active")})}function closeBottomPanel(){let e=$(".js-info-block");$(document).on("click",".js-close-info",function(o){o.preventDefault(),e.removeClass("is-active")})}function playVideo(){$(document).on("click",".js-play",function(e){e.preventDefault(),$("#video").trigger("play"),$(".video-holder").addClass("video-play"),$("#video").attr("controls","controls")})}$(document).ready(function(){$("body");closeMobMenu(),openMobMenu(),closeTopPanel(),closeBottomPanel(),$("#video")&&playVideo(),AOS.init()});