function closeMobMenu(){let e=$("body");$(document).on("click",".js-close-menu",function(n){n.preventDefault(),e.removeClass("opened-menu")})}function openMobMenu(){let e=$("body");$(document).on("click",".js-open-menu",function(n){n.preventDefault(),e.addClass("opened-menu")})}function closeTopPanel(){let e=$(".top-panel");$(document).on("click",".js-top-panel",function(n){n.preventDefault(),e.removeClass("is-active")})}function closeBottomPanel(){let e=$(".js-info-block");$(document).on("click",".js-close-info",function(n){n.preventDefault(),e.removeClass("is-active")})}$(document).ready(function(){$("body");closeMobMenu(),openMobMenu(),closeTopPanel(),closeBottomPanel()});