const btnBurger=document.querySelector(".js-open-menu"),btnBurgerClose=document.querySelector(".js-close-menu"),mainBody=document.querySelector(".body"),topPanelBtn=document.querySelector(".js-top-panel"),topPanel=document.querySelector(".top-panel");btnBurger.addEventListener("click",function(){mainBody.classList.add("opened-menu")}),btnBurgerClose.addEventListener("click",function(){mainBody.classList.remove("opened-menu")}),topPanelBtn.addEventListener("click",function(){topPanel.classList.remove("is-active")});